package com.example.leonardo.guia_movil_turismo;
import android.content.Intent;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.media.Image;
import android.util.Base64;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.TextView;

import java.io.ByteArrayOutputStream;

import static android.graphics.BitmapFactory.decodeResource;

public class Galeria extends AppCompatActivity {

    public static final String imagenString = "";


    public String encodedImage=null;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_galeria);

        TextView textView = (TextView) findViewById(R.id.txt_bb);
        encode();
        //textView.setText(encodedImage);
        decodeBase64();
    }

    public void decodeBase64()
    {
        byte[] decode = Base64.decode(encodedImage,Base64.DEFAULT);


        Bitmap bmp = BitmapFactory.decodeByteArray(decode, 0, decode.length);
        ImageView image = (ImageView) findViewById(R.id.imagenp);

        image.setImageBitmap(bmp);
    }


    public void encode() {

        Bitmap bm = decodeResource(getResources(), R.drawable.provincia_de_santa_elena);
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        bm.compress(Bitmap.CompressFormat.JPEG, 100, baos); //bm is the bitmap object
        byte[] b = baos.toByteArray();

        encodedImage = android.util.Base64.encodeToString(b, android.util.Base64.DEFAULT);
        //return encodedImage;
        //editText.setText(encodedImage);

    }
}
